style:
.gtif-footer {
            color: #FFF9;
        }
        .gtif-footer h3 {
            text-transform: uppercase;
 margin-bottom: 18px;
            font-size: 20px;
            font-weight: 400;
            color: #FFF9;
            text-align: left;
        }

        .gtif-footer a, .gtif-footer span {
          text-decoration: none; border-bottom: none; cursor: pointer;
          color: #FFF;
        }

        .gtif-footer p {
          text-align: left;

          font-size: 20px;
 padding: 0;        }

        .gtif-footer .social-icon {
            height: 24px;
            margin-right: 16px;
        }

        .gtif-footer .social-icon path {
            fill: #FFFA;
        }

        .gtif-footer button {
            color: #EEE;
            background: transparent;
            border: 1px solid #FFF9;
        }

