style:
.domainpage {
        font-family: 'NotesESABold';
    }
    .flex {
        display: flex;
    }

    .scrolly-row {
        flex-direction: row;
    }

    .scrolly-col {
        flex-direction: column;
    }

    .justify-center {
        justify-content: center;
    }

    .justify-start {
        justify-content: start;
    }

    .justify-end {
        justify-content: end;
    }

    .justify-between {
        justify-content: space-between;
    }

    .justify-around {
        justify-content: space-around;
    }

    .items-center {
        align-items: center;
    }

    .fill-width {
        width: 100%;
    }

    .fill-height {
        height: 100%;
    }

    .demonstrator-domains {
        position: relative;
        height: 56px;
    }

    .demonstrator-domains .tag {
        position: absolute;
        right: 50px;
        height: 56px;
        color: #338F83;
        background: #D9E4E7;
        text-transform: uppercase;
        font-family: 'NotesESA';
        font-size: 18px;
        padding: 0 15px;
    }

    .flag {
        width: 36px;
        height: 36px;
        border-radius: 50%;
        margin-left: 12px;
        overflow: hidden;
    }

    .flag .red {
        display: inline-flex;
        width: 100%;
        height: 33.33333%;
        background: #f55;
    }

    .flag .white {
        display: inline-flex;
        width: 100%;
        height: 33.33333%;
        background: #fff;
    }

methods.headerImage:
function () {
            switch (this.routeName) {
                case 'gtif-energy-transition':
                case 'gtif-sustainable-cities':
                case 'gtif-mobility-transition':
                case 'gtif-social-mobility':
                case 'gtif-eo-adaptation-services':
                case 'gtif-carbon-accounting':
                    return this.routeName.replace('gtif-', '');
                    break;

                default:
                    console.error('Header image can only be rendered on a GTIF domain page');
            }
        }
